SELECT TOP 100 PERCENT * FROM (
SELECT * FROM (SELECT TOP 100 PERCENT * FROM ( 
SELECT [GEOINTERVAL].[HOLEID], 
[GEOINTERVAL].[PROJECTCODE], [GEOINTERVAL].[GEOLFROM], 
[GEOINTERVAL].[GEOLTO], [GEOINTERVAL].[PRIORITY], 
[GEODETAILS].[Altern1], [GEODETAILS].[Altern1_Intnsty], 
[GEODETAILS].[Altern2], [GEODETAILS].[Altern2_Intnsty], 
[GEODETAILS].[Colour], [GEODETAILS].[Lith1], 
[GEODETAILS].[Lith2], [GEODETAILS].[Mineral1], 
[GEODETAILS].[Mineral1_Perct], 
[GEODETAILS].[Mineral1_Style], 
[GEODETAILS].[Mineral2], [GEODETAILS].[Mineral2_Perct], 
[GEODETAILS].[Mineral2_Style], 
[GEOCOMMENT].[Photo], [GEODETAILS].[Texture] FROM GEOINTERVAL INNER JOIN (
SELECT * FROM [HOLELOCATION]) AS [CollarWSF] ON [CollarWSF].[HOLEID] = [GEOINTERVAL].[HOLEID] AND [CollarWSF].[PROJECTCODE] = [GEOINTERVAL].[PROJECTCODE] AND [CollarWSF].[HOLETYPE] = 'DRILLHOLE' LEFT JOIN (SELECT [GEODETAILS].[HOLEID], 
[GEODETAILS].[PROJECTCODE], [GEODETAILS].[GEOLFROM], 
[GEODETAILS].[GEOLTO], [GEODETAILS].[PRIORITY], 
min(CASE when [GEODETAILS].[NAME] = 'Altern1' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern1], 
min(CASE when [GEODETAILS].[NAME] = 'Altern1_Intnsty' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern1_Intnsty], 
min(CASE when [GEODETAILS].[NAME] = 'Altern2' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern2], 
min(CASE when [GEODETAILS].[NAME] = 'Altern2_Intnsty' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern2_Intnsty], 
min(CASE when [GEODETAILS].[NAME] = 'Colour' then [GEODETAILS].[VALUE] ELSE NULL END) as [Colour], 
min(CASE when [GEODETAILS].[NAME] = 'Lith1' then [GEODETAILS].[VALUE] ELSE NULL END) as [Lith1], 
min(CASE when [GEODETAILS].[NAME] = 'Lith2' then [GEODETAILS].[VALUE] ELSE NULL END) as [Lith2], 
min(CASE when [GEODETAILS].[NAME] = 'Mineral1' then [GEODETAILS].[VALUE] ELSE NULL END) as [Mineral1], 
min(CASE when [GEODETAILS].[NAME] = 'Mineral1_Perct' then [GEODETAILS].[VALUE] ELSE NULL END) as [Mineral1_Perct], 
min(CASE when [GEODETAILS].[NAME] = 'Mineral1_Style' then [GEODETAILS].[VALUE] ELSE NULL END) as [Mineral1_Style], 
min(CASE when [GEODETAILS].[NAME] = 'Mineral2' then [GEODETAILS].[VALUE] ELSE NULL END) as [Mineral2], 
min(CASE when [GEODETAILS].[NAME] = 'Mineral2_Perct' then [GEODETAILS].[VALUE] ELSE NULL END) as [Mineral2_Perct], 
min(CASE when [GEODETAILS].[NAME] = 'Mineral2_Style' then [GEODETAILS].[VALUE] ELSE NULL END) as [Mineral2_Style], 
min(CASE when [GEODETAILS].[NAME] = 'Texture' then [GEODETAILS].[VALUE] ELSE NULL END) as [Texture] FROM [GEODETAILS] GROUP BY [GEODETAILS].[PROJECTCODE], 
[GEODETAILS].[HOLEID], [GEODETAILS].[GEOLFROM], 
[GEODETAILS].[GEOLTO], [GEODETAILS].[PRIORITY])[GEODETAILS] ON [GEOINTERVAL].[PROJECTCODE] = [GEODETAILS].[PROJECTCODE] AND [GEOINTERVAL].[HOLEID] = [GEODETAILS].[HOLEID] AND [GEOINTERVAL].[GEOLFROM] = [GEODETAILS].[GEOLFROM] AND [GEOINTERVAL].[GEOLTO] = [GEODETAILS].[GEOLTO] AND [GEOINTERVAL].[PRIORITY] = [GEODETAILS].[PRIORITY] LEFT JOIN (SELECT [GEOCOMMENT].[HOLEID], 
[GEOCOMMENT].[PROJECTCODE], [GEOCOMMENT].[GEOLFROM], [GEOCOMMENT].[GEOLTO], 
[GEOCOMMENT].[PRIORITY], min(CASE when [GEOCOMMENT].[NAME] = 'Photo' then [GEOCOMMENT].[VALUE] ELSE NULL END) as [Photo] FROM [GEOCOMMENT] WHERE [GEOCOMMENT].[NAME] IN ('Photo') GROUP BY [GEOCOMMENT].[PROJECTCODE], 
[GEOCOMMENT].[HOLEID], [GEOCOMMENT].[GEOLFROM], [GEOCOMMENT].[GEOLTO], 
[GEOCOMMENT].[PRIORITY])[GEOCOMMENT] ON [GEOINTERVAL].[PROJECTCODE] = [GEOCOMMENT].[PROJECTCODE] AND [GEOINTERVAL].[HOLEID] = [GEOCOMMENT].[HOLEID] AND [GEOINTERVAL].[GEOLFROM] = [GEOCOMMENT].[GEOLFROM] AND [GEOINTERVAL].[GEOLTO] = [GEOCOMMENT].[GEOLTO] AND [GEOINTERVAL].[PRIORITY] = [GEOCOMMENT].[PRIORITY] WHERE ([Altern1] IS NOT NULL OR [Altern1_Intnsty] IS NOT NULL OR [Altern2] IS NOT NULL OR [Altern2_Intnsty] IS NOT NULL OR [Colour] IS NOT NULL OR [Lith1] IS NOT NULL OR [Lith2] IS NOT NULL OR [Mineral1] IS NOT NULL OR [Mineral1_Perct] IS NOT NULL OR [Mineral1_Style] IS NOT NULL OR [Mineral2] IS NOT NULL OR [Mineral2_Perct] IS NOT NULL OR [Mineral2_Style] IS NOT NULL OR [Photo] IS NOT NULL OR [Texture] IS NOT NULL) ) [ACQTMP]  ) AS [TMPVIEW9]) AS [TMPVIEW99]  WHERE (([TMPVIEW99].[PROJECTCODE] = 'Merlot') AND ([TMPVIEW99].[HOLEID] IN ('SRC122', 
'SRC123', 'RPN123', 'RPN124'))) ORDER BY [TMPVIEW99].[PROJECTCODE] ASC, 
[TMPVIEW99].[HOLEID] ASC, [TMPVIEW99].[GEOLFROM] ASC