SELECT TOP 100 PERCENT * FROM (
SELECT * FROM (SELECT TOP 100 PERCENT * FROM ( 
SELECT [GEOINTERVAL].[HOLEID], 
[GEOINTERVAL].[PROJECTCODE], [GEOINTERVAL].[GEOLFROM], 
[GEOINTERVAL].[GEOLTO], [GEOINTERVAL].[PRIORITY], 
[GEODETAILS].[Altern1], [GEODETAILS].[Altern1_Intnsty], 
[GEODETAILS].[Altern2], [GEODETAILS].[Altern2_Intnsty], 
[GEODETAILS].[Colour], [GEODETAILS].[Texture], 
[GEODETAILS].[Weathng] FROM GEOINTERVAL INNER JOIN (
SELECT * FROM [HOLELOCATION]) AS [CollarWSF] ON [CollarWSF].[HOLEID] = [GEOINTERVAL].[HOLEID] AND [CollarWSF].[PROJECTCODE] = [GEOINTERVAL].[PROJECTCODE] AND [CollarWSF].[HOLETYPE] = 'DRILLHOLE' LEFT JOIN (SELECT [GEODETAILS].[HOLEID], 
[GEODETAILS].[PROJECTCODE], [GEODETAILS].[GEOLFROM], [GEODETAILS].[GEOLTO], 
[GEODETAILS].[PRIORITY], min(CASE when [GEODETAILS].[NAME] = 'Altern1' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern1], 
min(CASE when [GEODETAILS].[NAME] = 'Altern1_Intnsty' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern1_Intnsty], 
min(CASE when [GEODETAILS].[NAME] = 'Altern2' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern2], 
min(CASE when [GEODETAILS].[NAME] = 'Altern2_Intnsty' then [GEODETAILS].[VALUE] ELSE NULL END) as [Altern2_Intnsty], 
min(CASE when [GEODETAILS].[NAME] = 'Colour' then [GEODETAILS].[VALUE] ELSE NULL END) as [Colour], 
min(CASE when [GEODETAILS].[NAME] = 'Texture' then [GEODETAILS].[VALUE] ELSE NULL END) as [Texture], 
min(CASE when [GEODETAILS].[NAME] = 'Weathng' then [GEODETAILS].[VALUE] ELSE NULL END) as [Weathng] FROM [GEODETAILS] WHERE [GEODETAILS].[NAME] IN ('Altern1', 
'Altern1_Intnsty', 'Altern2', 
'Altern2_Intnsty', 'Colour', 'Texture', 
'Weathng') GROUP BY [GEODETAILS].[PROJECTCODE], 
[GEODETAILS].[HOLEID], [GEODETAILS].[GEOLFROM], [GEODETAILS].[GEOLTO], 
[GEODETAILS].[PRIORITY])[GEODETAILS] ON [GEOINTERVAL].[PROJECTCODE] = [GEODETAILS].[PROJECTCODE] AND [GEOINTERVAL].[HOLEID] = [GEODETAILS].[HOLEID] AND [GEOINTERVAL].[GEOLFROM] = [GEODETAILS].[GEOLFROM] AND [GEOINTERVAL].[GEOLTO] = [GEODETAILS].[GEOLTO] AND [GEOINTERVAL].[PRIORITY] = [GEODETAILS].[PRIORITY] WHERE ([Altern1] IS NOT NULL OR [Altern1_Intnsty] IS NOT NULL OR [Altern2] IS NOT NULL OR [Altern2_Intnsty] IS NOT NULL OR [Colour] IS NOT NULL OR [Texture] IS NOT NULL OR [Weathng] IS NOT NULL) ) [ACQTMP]  ) AS [TMPVIEW10]) AS [TMPVIEW99]  WHERE (([TMPVIEW99].[PROJECTCODE] = 'Merlot') AND ([TMPVIEW99].[HOLEID] IN ('SRC122', 
'SRC123', 'RPN123', 'RPN124'))) ORDER BY [TMPVIEW99].[PROJECTCODE] ASC, 
[TMPVIEW99].[HOLEID] ASC, [TMPVIEW99].[GEOLFROM] ASC